import React, { Component } from 'react';
import { render } from 'react-dom';
import Hello from './Hello';
import './style.css';
import Comment from './Comment';
const API_KEY = "723d42961499c3de4b2beb522291d9cd";
class App extends Component {
  constructor() {
    super();
    this.state = {
      cityname: ''
    };
  }

setName=(event)=>{
  console.log(this.state.cityname)
  this.setState({
    cityname:event.target.value
  })
}
checkWeather = async(e)=>{
  e.preventDefault();
  //getElementById("comments").style.display = 'default';
console.log(this.state.cityname)
const response=await fetch(`api.openweathermap.org/data/2.5/weather?q=${this.state.cityname},&appid=${API_KEY}&units=metric`);
console.log(response);
const myJson=await response.json();
console.log(myJson);
// this.setState({
//   gender:myJson.gender
// })

}


  render() {
    return (
      <div><h3>
      Weather App
      </h3>

     <input placeholder ="Enter City Name..."onChange={()=>this.setName(event)}value={this.state.cityname}/>
   <button id ="search" onClick={this.checkWeather}>Search</button>

   <br/><br/>
   <div id = "comments">
 <Comment color='red' />
 </div>
      </div>
    );
  }
}

render(<App />, document.getElementById('root'));
