import React, { Component } from 'react';
import './index.css';
import Form from "./Form";
import Comment from './Comment';
import Weather from "./Weather";

const API_KEY = "723d42961499c3de4b2beb522291d9cd";

class App extends Component {
state = {
  temperature : undefined,
  pressure: undefined,
 Maxtemp:undefined,
 Mintemp: undefined,
 weather: undefined,
  humidity: undefined,
  wind: undefined,
  sunrise: undefined,
  sunset: undefined,
  error: undefined
}
getWeather = async(e)=>{
  e.preventDefault();
const city = e.target.elements.city.value;
  //getElementById("comments").style.display = 'default';
console.log(city)
const response=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`);
console.log(response);
const myJson=await response.json();
console.log(myJson);
const date = new Date(myJson.sys.sunrise);
console.log(date);
this.setState({
  temperature: myJson.main.temp,
  humidity: myJson.main.humidity,
  Maxtemp: myJson.main.temp_max,
  Mintemp: myJson.main.temp.min,
  pressure: myJson.main.pressure,
  weather: myJson.weather[0].main,
  wind: myJson.wind.speed,
  sunrise: myJson.sys.sunrise,
  sunset: myJson.sys.sunset,
   error: ""
});
}


  render() {
    return (
      <div><h3>
      Weather App
      </h3>
    <Form getWeather={this.getWeather}/>
<div id="information">
  <Weather

  temperature = {this.state.temperature}
  weather = {this.state.weather}
  wind = {this.state.wind}
  humidity = {this.state.humidity}
  pressure = {this.state.pressure}
  Maxtemp = {this.state.Maxtemp}
  Mintemp = {this.state.Mintemp}
  sunrise = {this.state.sunrise}
  sunset = {this.state.sunset}
  error = {this.state.error}
  />
</div>
   <br/><br/>
   <div id = "comments">
 <Comment color='red' />
 </div>
      </div>
    );
  }
};

export default App;
